package myfirstproject;

public class restar {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int a=0; a<4; a++) {
			for(int b=0; b<7; b++) {
				if(7/2-b>=a||7/2-b<=a) {
					System.out.println("*");
				}else System.out.println(" ");

				System.out.print(b);


			}System.out.println();
		}

	}
}

